sect3.programlisting:
vector<mystruct> v;
Query q = connection.query();
q << "SELECT * FROM mytable";
q.storein(v);
for (vector<mystruct>::iterator it = v.begin(); it != v.end(); ++it) {
    cout << "Price: " << it->price << endl;
}

